mapper.select:
SELECT sn.*,le.emp_number as emp_number, le.emp_mobile as empMobile
from LX_MANAGER sn
LEFT JOIN  lx_emp le ON le.emp_id = sn.emp_id
WHERE 1=1

    AND sn.is_use = #{is_use}

limit #{index},#{size}

SELECT count(sn.manager_id) FROM LX_MANAGER sn
WHERE 1=1

    AND sn.is_use = #{is_use}
