mapper.select:
SELECT sn.*,le.emp_name as emp_name, le.emp_cover as emp_cover,lgg.cloud_caoping_guige_cont as cloud_caoping_guige_cont
,luse.cloud_caoping_use_cont as cloud_caoping_use_cont,ltype.cloud_caoping_type_cont as cloud_caoping_type_cont
from cloud_caoping sn
LEFT JOIN lx_emp le ON le.emp_id = sn.emp_id
LEFT JOIN cloud_caoping_guige lgg ON lgg.cloud_caoping_guige_id = sn.cloud_caoping_guige_id
LEFT JOIN cloud_caoping_use luse ON luse.cloud_caoping_use_id = sn.cloud_caoping_use_id
LEFT JOIN cloud_caoping_type ltype ON ltype.cloud_caoping_type_id = sn.cloud_caoping_type_id
where 1 = 1

    AND sn.emp_id = #{emp_id}



    AND sn.cloud_caoping_guige_id = #{cloud_caoping_guige_id}



    AND sn.cloud_caoping_use_id = #{cloud_caoping_use_id}



    AND sn.cloud_caoping_type_id = #{cloud_caoping_type_id}


    AND sn.cloud_caoping_is_use = #{cloud_caoping_is_use}


    AND sn.cloud_caoping_is_del = #{cloud_caoping_is_del}



    AND (sn.cloud_caoping_title like CONCAT('%',#{keyWords},'%') OR sn.cloud_caoping_content like CONCAT('%',#{keyWords},'%') OR sn.cloud_caoping_address like CONCAT('%',#{keyWords},'%') OR le.emp_name like CONCAT('%',#{keyWords},'%') )



    AND sn.lat > (#{lat}-1) AND   (#{lat} + 1) > sn.lat


    AND sn.lng > (#{lng}-1) AND   (#{lng} + 1) > sn.lng


     ORDER BY sn.cloud_caoping_dateline DESC


     ORDER BY sn.sale_count DESC

limit #{index},#{size}
