mapper.select:
SELECT le.*,ll.level_name as levelName, latt.lx_attribute_nick as lx_attribute_nick ,lc.count as jfcount,lpac.package_money as package_money, lcom.company_id as company_id,lcom.company_name as company_name, lcom.company_person as company_person,lcom.company_tel as company_tel, lcom.company_address as company_address,lcom.lat_company as lat_company,lcom.lng_company as lng_company,lcom.company_pic as company_pic , lcom.is_check as is_check,lcom.provinceid as provinceid ,lcom.cityid as cityid,lcom.areaid as areaid from lx_emp le LEFT JOIN lx_level ll ON ll.level_id = le.level_id LEFT JOIN lx_attribute latt ON latt.lx_attribute_id = le.lx_attribute_id LEFT JOIN lx_count lc ON lc.emp_id = le.emp_id LEFT JOIN lx_package lpac ON lpac.emp_id = le.emp_id LEFT JOIN company lcom ON lcom.emp_id = le.emp_id WHERE 1=1
AND le.emp_mobile=#{phoneNumber}
AND le.level_id=#{level_id}
AND le.emp_name like CONCAT('%',#{keyWords},'%')
ORDER BY le.dateline DESC limit #{index},#{size}

select count(le.emp_id) from lx_emp le
WHERE 1=1


    AND le.emp_mobile=#{phoneNumber}

select * from lx_emp where '1'='1'

    AND emp_mobile=#{keyWords} or emp_name like CONCAT('%',#{keyWords},'%')

limit #{index},#{size}

mapper.update:
UPDATE lx_emp SET
is_card_emp=#{is_card_emp}
WHERE emp_id=#{empId}
