mapper.select:
SELECT lapp.*, lemp.emp_name as emp_name, lleng.car_length_name as car_length_name, lcartype.car_type_name as car_type_name ,lp.pname as start_pname,lcity.cityName as start_cityName,larea.areaName as start_areaName ,lpp.pname as end_pname,lcityp.cityName as end_cityName,lareap.areaName as end_areaName
from car_transport lapp LEFT JOIN lx_emp lemp ON lemp.emp_id = lapp.emp_id LEFT JOIN province lp ON lp.provinceid = lapp.start_provinceid LEFT JOIN city lcity ON lcity.cityid = lapp.start_cityid LEFT JOIN area larea ON larea.areaid = lapp.start_areaid
LEFT JOIN province lpp ON lpp.provinceid = lapp.end_provinceid LEFT JOIN city lcityp ON lcityp.cityid = lapp.end_cityid LEFT JOIN area lareap ON lareap.areaid = lapp.end_areaid
LEFT JOIN car_length lleng ON lleng.car_length_id = lapp.car_length_id LEFT JOIN car_type lcartype ON lcartype.car_type_id = lapp.car_type_id
WHERE 1=1
AND lapp.emp_id = #{emp_id}
AND lapp.is_type = #{is_type}
AND lapp.is_use = #{is_use}
AND lapp.is_del = #{is_del}
AND lapp.start_areaid = #{areaidStart}
AND lapp.end_areaid = #{areaidEnd}
AND (lemp.emp_name like CONCAT('%',#{keyWords},'%') OR lp.pname like CONCAT('%',#{keyWords},'%') OR lcity.cityName like CONCAT('%',#{keyWords},'%') OR larea.areaName like CONCAT('%',#{keyWords},'%') OR lpp.pname like CONCAT('%',#{keyWords},'%') OR lcityp.cityName like CONCAT('%',#{keyWords},'%') OR lareap.areaName like CONCAT('%',#{keyWords},'%') OR lcartype.car_type_name like CONCAT('%',#{keyWords},'%') )
AND lapp.lat > (#{lat}-1) AND (#{lat} + 1) > lapp.lat
AND lapp.lng > (#{lng}-1) AND (#{lng} + 1) > lapp.lng
ORDER BY lapp.dateline DESC
limit #{index},#{size}

SELECT count(apply_gys_id) from car_transport lapp LEFT JOIN lx_emp lemp ON lemp.emp_id = lapp.emp_id LEFT JOIN province lp ON lp.provinceid = lapp.start_provinceid LEFT JOIN city lcity ON lcity.cityid = lapp.start_cityid LEFT JOIN area larea ON larea.areaid = lapp.start_areaid
LEFT JOIN province lpp ON lpp.provinceid = lapp.end_provinceid LEFT JOIN city lcityp ON lcityp.cityid = lapp.end_cityid LEFT JOIN area lareap ON lareap.areaid = lapp.end_areaid
LEFT JOIN car_length lleng ON lleng.car_length_id = lapp.car_length_id LEFT JOIN car_type lcartype ON lcartype.car_type_id = lapp.car_type_id
WHERE 1=1
AND lapp.emp_id = #{emp_id}
AND lapp.is_type = #{is_type}
AND lapp.is_use = #{is_use}
AND lapp.is_del = #{is_del}
AND lapp.start_areaid = #{areaidStart}
AND lapp.end_areaid = #{areaidEnd}
AND (lemp.emp_name like CONCAT('%',#{keyWords},'%') OR lp.pname like CONCAT('%',#{keyWords},'%') OR lcity.cityName like CONCAT('%',#{keyWords},'%') OR larea.areaName like CONCAT('%',#{keyWords},'%') OR lpp.pname like CONCAT('%',#{keyWords},'%') OR lcityp.cityName like CONCAT('%',#{keyWords},'%') OR lareap.areaName like CONCAT('%',#{keyWords},'%') OR lcartype.car_type_name like CONCAT('%',#{keyWords},'%') )
AND lapp.lat > (#{lat}-1) AND (#{lat} + 1) > lapp.lat
AND lapp.lng > (#{lng}-1) AND (#{lng} + 1) > lapp.lng
